import { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import Todo from './Todo'; //Todo 컴포넌트를 사용하기 위해 import
import { Example } from './Example'
import { Greeting, Farewell } from './Gretting';


function App() {
  // 하나의 할 일을 객체로 관리할 것이다
  // id, title, done
  const [items, setItem] = useState([{
    id:"0",
    title:"Hello world 1",
    done: true
  },
  {
    id:"1",
    title:"Hello world 2",
    done: false
  }])

  // react는 key속성에 들어있는 값을 참고해서, 리스트의 요소가 변경될 경우 어떤 요소가 변경되었는지
  // 빠르게 파악할 수 있다.
  const todoItems = items.length > 0 && items.map(item => <Todo item={item} key={item.id} />)

  
  return (
    <div className="App">
      <Todo item={todoItems}/>
    </div>
  );
  
}

export default App;
